mapper.select:
select * from BASE_APP_USER WHERE 1= 1 and ISDELETE = 0

    and ORGANID = #{organid}


	limit #{offset}, #{limit}

order by sort

select u.* from BASE_APP_USER u inner join DOCUMENT_BANJIAN_TREE_SET s
on u.user_id=s.user_id where ISDELETE = 0

    and u.ORGANID = #{organid}


	limit #{offset}, #{limit}

order by u.sort

select * from BASE_APP_USER u join XLTX_JLB jlb on jlb.btxry = u.id where u.ISDELETE = 0 and u.organid = #{id} and jlb.tableId=#{tableId}

	and DateDiff(dd,datetime,#{datetime})=0


	and txry = #{txry}

select count(grzt) from XLTX_JLB jlb left join BASE_APP_USER u on jlb.btxry = u.id left join BASE_APP_ORGAN organ on u.ORGANID = organ.id left join XLTX_TABLE ta on jlb. tableId = ta.id where tree_path like concat('%',#{id},'%') and ta.id=#{tableId} and u.ISDELETE = 0 and organ.ISDELETE = 0
and DateDiff(dd,jlb.datetime,#{datetime})=0
and grzt = #{grzt}
and jlb.txry = #{txry}

select a.id, a.USER_ID as userId, a.TRUENAME as truename, a.ORGANID as organid, a.SECLEVEL as seclevel, a.SEX as sex, a.ISDELETE as isdelete, b.name as organName from BASE_APP_USER a join BASE_APP_ORGAN b on a.ORGANID = b.id WHERE 1= 1 and a.ISDELETE = 0
and a.ORGANID = #{organid}
and b.TREE_PATH like '%'||#{treePath}||'%'
order by b.sort,a.sort
limit #{offset}, #{limit}
